truncate table disease_history cascade ;
update disease_history set coming_time = now() - interval '2 days';
update disease_history set coming_time = now() - interval '2 months' where patient_id=7;
update disease_history set coming_time = now() - interval '1 months' where patient_id=8;
update disease_history set coming_time = now() - interval '3 months' where patient_id=9;
update disease_history set coming_time = now() - interval '5 months' where patient_id=10;
update disease_history set coming_time = now() - interval '4 months' where patient_id=11;
update disease_history set coming_time = now() - interval '2 months' where patient_id=12;
update disease_history set coming_time = now() - interval '5 months' where patient_id=13;
update disease_history set release_time = now() where patient_status != 'SICK'::patient_status_type;
update disease_history set release_time = now() + interval '1 days' where patient_status = 'OUT_TREATMENT'::patient_status_type;
update disease_history set release_time = now() + interval '10 minutes' where patient_status = 'ANOTHER_PLACE'::patient_status_type;

